import { useState } from 'react'
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom'
import StartPage from './components/StartPage';
import Header from './components-from-crash-course/Header';
import Footer from './components-from-crash-course/Footer';
import About from './components-from-crash-course/About';
import './App.css';



const App = () => {
  return (
    <Router>
      <div className='container'>
        <Header
          showAdd={true}
        />
        <Routes>
          <Route
            path='/'
            element={
              <>
                {showAddTask && <AddTask onAdd={addTask} />}
                {tasks.length > 0 ? (
                  <Tasks
                    tasks={tasks}
                    onDelete={deleteTask}
                    onToggle={toggleReminder}
                  />
                ) : (
                  'No Tasks To Show'
                )}
              </>
            }
          />
          <Route path='/about' element={<About />} />
        </Routes>
        <Footer />
      </div>
    </Router>
  )
}





export default App;
